import React, { useState } from "react";
import { getActividadesPorModulo } from "../../services/activityService";
import LucesCalma from "./LucesCalma";
import ReaccionSensorial from "./ReaccionSensorial";
import PulsoMusical from "./PulsoMusical";
import "./Sensorial.css";

export default function Sensorial() {
  const actividades = [
    {
      name: "Sinfon√≠a de Colores",
      description:
        "Repite la secuencia de luces y sonidos para mejorar tu memoria sensorial.",
      why_useful:
        "Desarrolla la atenci√≥n, memoria auditiva y visual al combinar ritmo y color.",
      difficulty: "F√°cil",
      stimulus: "Visual / Auditivo",
    },
    ...getActividadesPorModulo("Sensorial"), // mantiene las otras actividades
  ];

  const [actividadSeleccionada, setActividadSeleccionada] = useState(null);

  const renderActividad = () => {
    switch (actividadSeleccionada?.name) {
      case "Luces que calman":
        return <LucesCalma volver={() => setActividadSeleccionada(null)} />;
      case "Reacci√≥n sensorial":
        return <ReaccionSensorial volver={() => setActividadSeleccionada(null)} />;
      case "Pulso musical":
        return <PulsoMusical volver={() => setActividadSeleccionada(null)} />;
      default:
        return null;
    }
  };

  return (
    <div className="sensorial-container">
      {!actividadSeleccionada ? (
        <>
          <h1 className="sensorial-title">üåà M√≥dulo Sensorial</h1>
          <p className="sensorial-sub">Selecciona una actividad para comenzar</p>

          <div className="activity-grid">
            {actividades.map((a, i) => (
              <div
                key={i}
                className="activity-card fade-in"
                onClick={() => setActividadSeleccionada(a)}
              >
                <h2>{a.name}</h2>
                <p>{a.description}</p>
                <p className="why-useful">üéØ {a.why_useful}</p>
                <p className="meta">
                  <strong>Dificultad:</strong> {a.difficulty} |{" "}
                  <strong>Est√≠mulo:</strong> {a.stimulus}
                </p>
              </div>
            ))}
          </div>
        </>
      ) : (
        <div className="actividad-sensorial">
          {renderActividad()}
          <button
            className="boton-volver"
            onClick={() => setActividadSeleccionada(null)}
          >
            ‚Üê Volver al m√≥dulo Sensorial
          </button>
        </div>
      )}
    </div>
  );
}
