import React, { useState } from "react";
import "../../styles/Familiar.css";
import ConociendoFamilia from "./ConociendoFamilia";
import ResolviendoConflictos from "./ResolviendoConflictos";
import ApoyoMutuo from "./ApoyoMutuo";

export default function Familiar() {
  const [actividadActual, setActividadActual] = useState(null);

  if (actividadActual === "ConociendoFamilia") {
    return <ConociendoFamilia setActividadActual={setActividadActual} />;
  }

  if (actividadActual === "ResolviendoConflictos") {
    return <ResolviendoConflictos setActividadActual={setActividadActual} />;
  }

  if (actividadActual === "ApoyoMutuo") {
    return <ApoyoMutuo setActividadActual={setActividadActual} />;
  }

  return (
    <div className="familiar-container">
      <h1 className="familiar-title">🏠 Módulo Familiar</h1>
      <p className="familiar-descripcion">
        En este módulo aprenderás a fortalecer los vínculos familiares a través del respeto, la empatía
        y la comunicación asertiva. Aquí encontrarás actividades diseñadas para mejorar la convivencia
        y fomentar la comprensión mutua.
      </p>

      <div className="familiar-botones">
        <button
          className="familiar-boton"
          onClick={() => setActividadActual("ConociendoFamilia")}
        >
          Conociendo a mi familia
        </button>

        <button
          className="familiar-boton"
          onClick={() => setActividadActual("ResolviendoConflictos")}
        >
          Resolviendo Conflictos
        </button>

        <button
          className="familiar-boton"
          onClick={() => setActividadActual("ApoyoMutuo")}
        >
          Apoyo Mutuo
        </button>
      </div>
    </div>
  );
}
