import React, { useState, useEffect } from "react";
import "../../styles/Sensorial.css";

export default function LightChase({ volver }) {
  const [score, setScore] = useState(0);
  const [lights, setLights] = useState([]);
  const [gameActive, setGameActive] = useState(false);
  const [level, setLevel] = useState(1);
  const [message, setMessage] = useState("Haz clic en 'Comenzar' para iniciar");

  // Configuraci√≥n inicial del juego
  const initialInterval = 2000; // ‚è±Ô∏è 2 segundos entre luces al inicio
  const minInterval = 800;      // velocidad m√≠nima despu√©s de varios niveles
  const [intervalTime, setIntervalTime] = useState(initialInterval);

  // Generar luces
  useEffect(() => {
    if (!gameActive) return;

    const interval = setInterval(() => {
      const newLight = {
        id: Date.now(),
        x: Math.random() * 80 + 10,
        y: Math.random() * 70 + 10,
      };
      setLights((prev) => [...prev, newLight]);

      // Aumentar dificultad cada 5 aciertos, pero lentamente
      if (score > 0 && score % 5 === 0 && intervalTime > minInterval) {
        setLevel((lvl) => lvl + 1);
        setIntervalTime((prev) => prev - 150); // reduce velocidad de aparici√≥n
        setMessage(`Nivel ${level + 1}: ¬°M√°s r√°pido!`);
      }
    }, intervalTime);

    return () => clearInterval(interval);
  }, [gameActive, score, intervalTime, level]);

  // Manejar clic en luz
  const handleLightClick = (id) => {
    setLights((prev) => prev.filter((l) => l.id !== id));
    setScore((prev) => prev + 1);
    setMessage("¬°Bien hecho!");
  };

  // Iniciar o reiniciar juego
  const startGame = () => {
    setScore(0);
    setLevel(1);
    setIntervalTime(initialInterval);
    setLights([]);
    setMessage("¬°Empieza la caza de luces!");
    setGameActive(true);
  };

  // Terminar juego manualmente
  const stopGame = () => {
    setGameActive(false);
    setMessage("Juego terminado. ¬°Buen trabajo!");
  };

  return (
    <div className="lightchase-container fade-in">
      <h2 className="sensorial-title">‚ú® Caza de Luz</h2>
      <p className="sensorial-descripcion">
        Haz clic en las luces que aparecen en pantalla antes de que desaparezcan.
        Empieza despacio y observa c√≥mo el ritmo aumenta poco a poco.
      </p>

      {!gameActive ? (
        <button className="sensorial-boton" onClick={startGame}>
          üöÄ Comenzar
        </button>
      ) : (
        <button className="sensorial-boton" onClick={stopGame}>
          ‚èπÔ∏è Detener
        </button>
      )}

      <div className="lightchase-score">
        <p>Puntuaci√≥n: {score}</p>
        <p>Nivel: {level}</p>
        <p>{message}</p>
      </div>

      {/* Luces interactivas */}
      {lights.map((light) => (
        <div
          key={light.id}
          className="light-dot"
          style={{
            top: `${light.y}%`,
            left: `${light.x}%`,
          }}
          onClick={() => handleLightClick(light.id)}
        ></div>
      ))}

      <button className="boton-volver" onClick={volver}>
        ‚Üê Volver al m√≥dulo sensorial
      </button>
    </div>
  );
}
