import React, { useState } from "react";
import "./Login.css";
import { useNavigate } from "react-router-dom";

export default function Login({ onLogin }) {
  const navigate = useNavigate();
  const [usuario, setUsuario] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // CONSULTAR USUARIOS DESDE LA BASE DE DATOS
      const response = await fetch('http://localhost:3001/api/users');
      const users = await response.json();

      // BUSCAR USUARIO QUE COINCIDA CON NOMBRE Y CONTRASE√ëA
      const userFound = users.find(user => 
        user.name === usuario && user.password === password
      );

      if (userFound) {
        navigate("/Dashboard");
      } else {
        setError("‚ùå Usuario o contrase√±a incorrectos");
      }
    } catch (error) {
      console.error('Error en login:', error);
      setError("‚ùå Error de conexi√≥n. Intenta nuevamente.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="login-title">‚ú® Bienvenido a CognIA</h1>
        <p className="login-subtitle">
          Mejora tus habilidades cognitivas y sociales
        </p>

        <form onSubmit={handleSubmit} className="login-form">
          <input
            type="text"
            placeholder="Usuario"
            value={usuario}
            onChange={(e) => setUsuario(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Contrase√±a"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {error && <p className="login-error">{error}</p>}

          <button type="submit" className="login-button">
            Ingresar
          </button>
        </form>

        <p className="login-footer">
          ¬© 2025 CognIA | Desarrollando habilidades con empat√≠a üíú
        </p>
      </div>
    </div>
  );
}
