import React, { useRef, useState, useEffect } from "react";
import "./Cognitivo.css";
import { useRegistroActividad } from "../../hooks/useRegistroActividad";

export default function DibujarFigura({ volver }) {
  const canvasRef = useRef(null);
  const [context, setContext] = useState(null);
  const [drawing, setDrawing] = useState(false);
  const [feedback, setFeedback] = useState("");
  const [figuraActual, setFiguraActual] = useState("");
  const [errores, setErrores] = useState(0);
  const [loading, setLoading] = useState(false);
  const { registrarExito, registrarFallo } = useRegistroActividad();
  const figuras = ["tri√°ngulo", "cuadrado", "c√≠rculo"];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.lineWidth = 3;
    ctx.lineCap = "round";
    ctx.strokeStyle = "#333";
    setContext(ctx);
    nuevaFigura();
  }, []);

  const nuevaFigura = () =>
    setFiguraActual(figuras[Math.floor(Math.random() * figuras.length)]);

  const start = (e) => {
    setDrawing(true);
    const { offsetX, offsetY } = e.nativeEvent;
    context.beginPath();
    context.moveTo(offsetX, offsetY);
  };

  const draw = (e) => {
    if (!drawing) return;
    const { offsetX, offsetY } = e.nativeEvent;
    context.lineTo(offsetX, offsetY);
    context.stroke();
  };

  const stop = () => setDrawing(false);

  const limpiar = () => {
    const c = canvasRef.current;
    context.clearRect(0, 0, c.width, c.height);
    setFeedback("");
  };

  const validar = async () => {
    setLoading(true);
    try {
      const img = canvasRef.current.toDataURL("image/png");
      const res = await fetch("http://127.0.0.1:8000/analyze_drawing", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ figuraEsperada: figuraActual, image: img }),
      });
      const data = await res.json();
      const ok = /correct|bien/i.test(data.feedback);
      setFeedback(ok ? "‚úÖ ¬°Excelente!" : "‚ùå Int√©ntalo de nuevo");

      if (ok) {
        registrarExito("Cognitivo", "Dibujar la figura", 3);
        nuevaFigura();
        limpiar();
        setErrores(0);
      } else {
        registrarFallo("Cognitivo", "Dibujar la figura", 3);
        setErrores((e) => e + 1);
      }
    } catch {
      setFeedback("‚ö†Ô∏è Error al analizar");
    } finally {
      setLoading(false);
    }
  };

  const mostrarHint = () => {
    const ctx = canvasRef.current.getContext("2d");
    ctx.strokeStyle = "#aaa";
    ctx.beginPath();
    if (figuraActual === "cuadrado") {
      ctx.rect(100, 100, 200, 200);
    } else if (figuraActual === "c√≠rculo") {
      ctx.arc(200, 200, 100, 0, Math.PI * 2);
    } else {
      ctx.moveTo(200, 100);
      ctx.lineTo(100, 300);
      ctx.lineTo(300, 300);
      ctx.closePath();
    }
    ctx.stroke();
  };

  useEffect(() => {
    if (errores >= 3) {
      mostrarHint();
      setFeedback("üí° Pista: as√≠ deber√≠a verse");
      setErrores(0);
    }
  }, [errores]);

  return (
    <div className="actividad-cognitiva">
      <h1>Dibuja la figura</h1>
      <p>Dibuja un {figuraActual}</p>

      <canvas
        ref={canvasRef}
        width={400}
        height={400}
        className="canvas-dibujo"
        onMouseDown={start}
        onMouseMove={draw}
        onMouseUp={stop}
        onMouseLeave={stop}
      />

      <div className="botones">
        <button onClick={validar} disabled={loading}>
          {loading ? "Analizando..." : "Validar"}
        </button>
        <button onClick={limpiar}>Limpiar</button>
        <button onClick={volver}>‚¨Ö Volver</button>
      </div>

      {feedback && <p className="feedback">{feedback}</p>}
    </div>
  );
}
