import React, { useState, useEffect, useRef } from "react";
import "./Sensorial.css";

export default function LucesCalma({ volver }) {
  const [running, setRunning] = useState(false);
  const [position, setPosition] = useState({ x: 50, y: 50 });
  const [score, setScore] = useState(0);
  const containerRef = useRef(null);

  // Movimiento del foco
  useEffect(() => {
    let interval;
    if (running) {
      interval = setInterval(() => {
        setPosition({
          x: 10 + Math.random() * 80, // porcentaje
          y: 10 + Math.random() * 70,
        });
      }, 2500); // cada 2.5 s se mueve
    }
    return () => clearInterval(interval);
  }, [running]);

  // Detectar si el mouse sigue el foco
  const handleMouseMove = (e) => {
    if (!containerRef.current || !running) return;
    const rect = containerRef.current.getBoundingClientRect();
    const foco = {
      x: (position.x / 100) * rect.width,
      y: (position.y / 100) * rect.height,
      r: 60,
    };

    const dist = Math.sqrt(
      Math.pow(e.clientX - rect.left - foco.x, 2) +
        Math.pow(e.clientY - rect.top - foco.y, 2)
    );

    if (dist < foco.r) {
      setScore((s) => s + 1);
    }
  };

  return (
    <div
      ref={containerRef}
      className="luces-calma"
      onMouseMove={handleMouseMove}
    >
      <h1 className="actividad-titulo">ðŸŒ… Luces que calman</h1>
      <p className="actividad-descripcion">
        Sigue con el cursor el foco de luz mientras se mueve.
      </p>

      {!running ? (
        <button className="boton-actividad" onClick={() => setRunning(true)}>
          Iniciar actividad
        </button>
      ) : (
        <button className="boton-actividad detener" onClick={() => setRunning(false)}>
          Detener
        </button>
      )}

      <div
        className="foco-luz"
        style={{
          left: `${position.x}%`,
          top: `${position.y}%`,
          opacity: running ? 1 : 0,
        }}
      ></div>

      <p className="puntaje">Seguimiento exitoso: {score}</p>

      <button className="boton-volver" onClick={volver}>
        â¬… Volver
      </button>
    </div>
  );
}
