import "./Sensorial.css";

export default function Sensorial() {
  const [actividadActual, setActividadActual] = useState(null);

  const renderActividad = () => {
    switch (actividadActual) {
      case "LightChase":
        return <LightChase volver={() => setActividadActual(null)} />;
