import React, { useState, useEffect, useRef } from "react";
import "./Sensorial.css";

export default function PulsoMusical({ volver }) {
  const [playing, setPlaying] = useState(false);
  const [score, setScore] = useState(0);
  const [beat, setBeat] = useState(0);
  const [feedback, setFeedback] = useState("");
  const intervalRef = useRef(null);
  const audioCtxRef = useRef(null);
  const nextBeatTimeRef = useRef(0);

  // Inicializa un sonido suave tipo "bip"
  const playTone = () => {
    if (!audioCtxRef.current) return;
    const ctx = audioCtxRef.current;
    const oscillator = ctx.createOscillator();
    const gain = ctx.createGain();
    oscillator.type = "sine";
    oscillator.frequency.setValueAtTime(440, ctx.currentTime); // La 440Hz
    gain.gain.setValueAtTime(0.05, ctx.currentTime); // volumen bajo
    oscillator.connect(gain);
    gain.connect(ctx.destination);
    oscillator.start();
    oscillator.stop(ctx.currentTime + 0.15);
  };

  const startGame = () => {
    if (!audioCtxRef.current) {
      audioCtxRef.current = new (window.AudioContext || window.webkitAudioContext)();
    }

    setPlaying(true);
    setScore(0);
    setBeat(0);
    setFeedback("");
    nextBeatTimeRef.current = audioCtxRef.current.currentTime + 1; // primer pulso despu√©s de 1s

    intervalRef.current = setInterval(() => {
      setBeat((prev) => prev + 1);
      playTone();
    }, 1500); // ritmo cada 1.5 segundos
  };

  const stopGame = () => {
    setPlaying(false);
    clearInterval(intervalRef.current);
    intervalRef.current = null;
  };

  const handlePress = () => {
    if (!audioCtxRef.current || !playing) return;
    const now = audioCtxRef.current.currentTime;
    const diff = Math.abs(now - nextBeatTimeRef.current);

    // entre 0.2s antes y 0.2s despu√©s cuenta como buen timing
    if (diff < 0.2) {
      setScore((prev) => prev + 1);
      setFeedback("¬°Buen ritmo! ü•Å");
    } else {
      setFeedback("Fuera de tiempo ‚è±Ô∏è");
    }
  };

  // Actualiza tiempo de referencia del siguiente beat
  useEffect(() => {
    if (playing) nextBeatTimeRef.current += 1.5;
  }, [beat, playing]);

  // Permitir tecla espacio
  useEffect(() => {
    const handleKey = (e) => {
      if (e.code === "Space") {
        e.preventDefault();
        handlePress();
      }
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  });

  return (
    <div className="pulso-musical">
      <h1 className="actividad-titulo">üéµ Pulso Musical</h1>
      <p className="actividad-descripcion">
        Presiona el bot√≥n (o la barra espaciadora) al ritmo del sonido.
        <br />Esta actividad ayuda a sincronizar o√≠do y movimiento.
      </p>

      {!playing ? (
        <button className="boton-actividad" onClick={startGame}>
          Iniciar ritmo
        </button>
      ) : (
        <button className="boton-actividad detener" onClick={stopGame}>
          Detener
        </button>
      )}

      <div className="zona-interaccion">
        <button className="boton-ritmo" onClick={handlePress}>
          üñê Pulsa al ritmo
        </button>
      </div>

      {feedback && <p className="feedback">{feedback}</p>}
      <p className="puntaje">Puntuaci√≥n: {score}</p>

      <button className="boton-volver" onClick={volver}>
        ‚¨Ö Volver
      </button>
    </div>
  );
}
