import React, { useState, useEffect } from "react";
import "../../styles/Sensorial.css";

export default function LightChase({ volver }) {
  const [lights, setLights] = useState([]);
  const [score, setScore] = useState(0);
  const [missed, setMissed] = useState(0);
  const [round, setRound] = useState(1);
  const [playing, setPlaying] = useState(false);

  // Parámetros del juego (más rondas = más dificultad)
  const lightsPerRound = 5;
  const lightDuration = Math.max(1500 - round * 100, 700); // cada ronda más rápido
  const boardSize = 400;

  useEffect(() => {
    if (playing) spawnLights();
  }, [playing, round]);

  const spawnLights = () => {
    const newLights = Array.from({ length: lightsPerRound }, () => ({
      id: Math.random(),
      x: Math.random() * (boardSize - 40),
      y: Math.random() * (boardSize - 40),
    }));
    setLights(newLights);

    // Remueve las luces después del tiempo límite
    const timer = setTimeout(() => {
      setMissed((m) => m + lightsPerRound);
      nextRound();
    }, lightDuration);

    return () => clearTimeout(timer);
  };

  const handleClick = (id) => {
    setScore((s) => s + 1);
    setLights((prev) => prev.filter((l) => l.id !== id));
    if (lights.length === 1) nextRound();
  };

  const nextRound = () => {
    setRound((r) => r + 1);
  };

  const startGame = () => {
    setScore(0);
    setMissed(0);
    setRound(1);
    setPlaying(true);
  };

  return (
    <div className="sensorial-container fade-in">
      {!playing ? (
        <>
          <h2 className="sensorial-title">✨ Caza de Luz</h2>
          <p className="sensorial-descripcion">
            ¡Atrapa las luces antes de que desaparezcan! Cuantas más atrapes, más rápido se moverán.
          </p>
          <button className="sensorial-boton" onClick={startGame}>
            ▶ Empezar juego
          </button>
          <button className="sensorial-boton" onClick={volver}>
            ← Volver al Módulo Sensorial
          </button>
        </>
      ) : (
        <>
          <div
            className="light-board"
            style={{
              width: boardSize,
              height: boardSize,
              margin: "1.5rem auto",
              position: "relative",
              background: "radial-gradient(circle at center, #111, #000)",
              borderRadius: "16px",
              boxShadow: "0 0 20px rgba(255,255,255,0.2)",
            }}
          >
            {lights.map((l) => (
              <div
                key={l.id}
                onClick={() => handleClick(l.id)}
                className="light-dot"
                style={{
                  left: l.x,
                  top: l.y,
                }}
              />
            ))}
          </div>

          <p className="sensorial-info">
            <strong>Puntaje:</strong> {score} | <strong>Falladas:</strong> {missed} |{" "}
            <strong>Ronda:</strong> {round}
          </p>

          <button className="sensorial-boton" onClick={volver}>
            ← Terminar juego
          </button>
        </>
      )}
    </div>
  );
}
