import React, { useRef, useState, useEffect } from "react";
import "./Cognitivo.css";
import { useRegistroActividad } from "../../hooks/useRegistroActividad"; // ‚úÖ Importa el hook

export default function DibujarFigura({ volver }) {
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [context, setContext] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [figuraActual, setFiguraActual] = useState("tri√°ngulo");
  const [strokes, setStrokes] = useState([]);
  const [loading, setLoading] = useState(false);

  // ‚úÖ Hook de registro
  const { registrarExito, registrarFallo } = useRegistroActividad();

  const figuras = ["tri√°ngulo", "cuadrado", "c√≠rculo"];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.lineWidth = 3;
    ctx.lineCap = "round";
    ctx.strokeStyle = "#333";
    setContext(ctx);
  }, []);

  // üîπ Estados nuevos
  const [errores, setErrores] = useState(0);

  // üîπ funci√≥n auxiliar: cambia figura aleatoriamente
  const cambiarFigura = () => {
    const nueva = figuras[Math.floor(Math.random() * figuras.length)];
    setFiguraActual(nueva);
  };

  // üîπ dentro de validarDibujo(), justo despu√©s de setFeedback(resultText);
  if (resultText.toLowerCase().includes("correct") || resultText.toLowerCase().includes("bien")) {
    registrarExito("Cognitivo", "Dibujar la figura", 3);
    setFeedback("‚úÖ ¬°Excelente!");
    setErrores(0);
    cambiarFigura(); // cambia figura tras acierto
    limpiarCanvas();
  } else {
    registrarFallo("Cognitivo", "Dibujar la figura", 3);
    setFeedback("‚ùå Intenta de nuevo");
    setErrores((e) => e + 1);
  }

  // üîπ hint visual si se equivoca mucho
  useEffect(() => {
    if (errores >= 3 && context) {
      const ctx = context;
      ctx.strokeStyle = "#aaa";
      ctx.lineWidth = 2;
      ctx.beginPath();

      if (figuraActual === "cuadrado") {
        ctx.rect(100, 100, 200, 200);
      } else if (figuraActual === "c√≠rculo") {
        ctx.arc(200, 200, 100, 0, Math.PI * 2);
      } else {
        ctx.moveTo(200, 100);
        ctx.lineTo(100, 300);
        ctx.lineTo(300, 300);
        ctx.closePath();
      }

      ctx.stroke();
      setFeedback("üí° Pista: as√≠ se ve un " + figuraActual);
      setErrores(0);
    }
  }, [errores, context]);

  const startDrawing = (e) => {
    setDrawing(true);
    const { offsetX, offsetY } = e.nativeEvent;
    context.beginPath();
    context.moveTo(offsetX, offsetY);
  };

  const draw = (e) => {
    if (!drawing) return;
    const { offsetX, offsetY } = e.nativeEvent;
    context.lineTo(offsetX, offsetY);
    context.stroke();

    setStrokes((prev) => [...prev, { x: offsetX, y: offsetY }]);
  };

  const stopDrawing = () => {
    setDrawing(false);
  };

  const limpiarCanvas = () => {
    const canvas = canvasRef.current;
    context.clearRect(0, 0, canvas.width, canvas.height);
    setStrokes([]);
    setFeedback("");
  };

  const validarDibujo = async () => {
    setLoading(true);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const tempCanvas = document.createElement("canvas");
    tempCanvas.width = canvas.width;
    tempCanvas.height = canvas.height;
    const tempCtx = tempCanvas.getContext("2d");

    tempCtx.fillStyle = "#ffffff";
    tempCtx.fillRect(0, 0, tempCanvas.width, tempCanvas.height);

    tempCtx.drawImage(canvas, 0, 0);

    const imageData = tempCanvas.toDataURL("image/png");

    const payload = {
      figuraEsperada: figuraActual,
      image: imageData,
    };

    try {
      const res = await fetch("http://127.0.0.1:8000/analyze_drawing", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();
      const resultText = data.feedback || "Sin respuesta del servidor";

      setFeedback(resultText);

      // ‚úÖ Registro seg√∫n el resultado
      if (resultText.toLowerCase().includes("correct") || resultText.toLowerCase().includes("bien")) {
        registrarExito("Cognitivo", "Dibujar la figura", 3);
      } else {
        registrarFallo("Cognitivo", "Dibujar la figura", 3);
      }

    } catch (err) {
      console.error("Error:", err);
      setFeedback("Error al analizar el dibujo");
      registrarFallo("Cognitivo", "Dibujar la figura", 3);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="actividad-cognitiva">
      <h1>Dibuja la figura</h1>
      <p>Instrucci√≥n: Dibuja un {figuraActual}</p>

      <canvas
        ref={canvasRef}
        width={400}
        height={400}
        className="canvas-dibujo"
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseLeave={stopDrawing}
      />

      <div className="botones">
        <button
          className="boton-actividad"
          onClick={validarDibujo}
          disabled={loading}
        >
          {loading ? "Analizando..." : "Validar dibujo"}
        </button>
        <button className="boton-actividad limpiar" onClick={limpiarCanvas}>
          Limpiar
        </button>
        <button className="boton-volver" onClick={volver}>
          ‚¨Ö Volver
        </button>
      </div>

      {feedback && <p className="feedback">{feedback}</p>}
    </div>
  );
}
