// src/modules/Familiar/Familiar.jsx
import React, { useState } from "react";
import "../../styles/Social.css"; // reutilizamos estilos del módulo social
import { getActividadesPorModulo } from "../../services/activityService";
import ConversacionEnFamilia from "./ConversacionEnFamilia";
import ResolviendoConflictos from "./ResolviendoConflictos";
import ApoyoMutuo from "./ApoyoMutuo";

export default function Familiar() {
  const [actividadSeleccionada, setActividadSeleccionada] = useState(null);
  const actividades = getActividadesPorModulo("Familiar");

  const renderActividad = () => {
    switch (actividadSeleccionada) {
      case "Conversación en Familia":
        return <ConversacionEnFamilia onVolver={() => setActividadSeleccionada(null)} />;
      case "Resolviendo Conflictos":
        return <ResolviendoConflictos onVolver={() => setActividadSeleccionada(null)} />;
      case "Apoyo Mutuo":
        return <ApoyoMutuo onVolver={() => setActividadSeleccionada(null)} />;
      default:
        return null;
    }
  };

  return (
    <div className="social-module fade-in">
      {!actividadSeleccionada ? (
        <>
          <header className="social-header">
            <h1>Módulo Familiar</h1>
            <div className="social-score">Fortalece tu entorno familiar</div>
          </header>

          <h2 className="lista-subtitulo">Selecciona una actividad:</h2>
          <ul className="lista-grid">
            {actividades.map((a, i) => (
              <li
                key={i}
                className="actividad-item card-hover"
                onClick={() => setActividadSeleccionada(a.name)}
              >
                <h3>{a.name}</h3>
                <p className="actividad-desc">{a.description}</p>
                <p className="actividad-meta">
                  Dificultad: {a.difficulty} | Estímulo: {a.stimulus}
                </p>
                <p className="actividad-extra">{a.why_useful}</p>
                <button className="boton-actividad">Comenzar</button>
              </li>
            ))}
          </ul>
        </>
      ) : (
        <div className="actividad-container fade-in">
          {renderActividad()}
        </div>
      )}
    </div>
  );
}
