import React, { useState, useEffect, useRef } from "react";
import "./PulsoMusical.css";

export default function PulsoMusical({ volver }) {
  const [playing, setPlaying] = useState(false);
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState("");
  const [highlight, setHighlight] = useState(false);
  const [bpm, setBpm] = useState(40); // ritmo inicial
  const [nivel, setNivel] = useState(1);
  const [combo, setCombo] = useState(0);

  const audioCtxRef = useRef(null);
  const intervalRef = useRef(null);
  const nextBeatRef = useRef(0);

  const getInterval = (bpm) => 60 / bpm;

  const playTone = (freq = 440) => {
    const ctx = audioCtxRef.current;
    const osc = ctx.createOscillator();
    const gain = ctx.createGain();
    osc.type = "sine";
    osc.frequency.setValueAtTime(freq, ctx.currentTime);
    gain.gain.setValueAtTime(0.1, ctx.currentTime);
    osc.connect(gain);
    gain.connect(ctx.destination);
    osc.start();
    osc.stop(ctx.currentTime + 0.15);
  };

  const start = () => {
    if (!audioCtxRef.current)
      audioCtxRef.current = new (window.AudioContext || window.webkitAudioContext)();

    setPlaying(true);
    setScore(0);
    setFeedback("");
    setNivel(1);
    setBpm(40);
    nextBeatRef.current = audioCtxRef.current.currentTime + 2; // espera 2s antes

    iniciarBeatLoop(40);
  };

  const iniciarBeatLoop = (nuevoBpm) => {
    const interval = getInterval(nuevoBpm);
    clearInterval(intervalRef.current);

    intervalRef.current = setInterval(() => {
      playTone(440 + nivel * 10); // cambia ligeramente el tono por nivel
      flash();
      nextBeatRef.current = audioCtxRef.current.currentTime + interval;
    }, interval * 1000);
  };

  const stop = () => {
    setPlaying(false);
    clearInterval(intervalRef.current);
  };

  const flash = () => {
    setHighlight(true);
    setTimeout(() => setHighlight(false), 200);
  };

  const handlePress = () => {
    if (!playing || !audioCtxRef.current) return;
    const now = audioCtxRef.current.currentTime;
    const diff = now - nextBeatRef.current;

    // üîπ Margen m√°s justo: ¬±0.25 s
    if (Math.abs(diff) <= 0.15) {
      setFeedback("üî• Perfecto!");
      setScore((s) => s + 3);
      setCombo((c) => c + 1);
    } else if (Math.abs(diff) <= 0.3) {
      setFeedback("‚úÖ Bien");
      setScore((s) => s + 1);
      setCombo(0);
    } else {
      setFeedback("‚ùå Fuera de ritmo");
      setCombo(0);
    }

    // üîπ Cada 10 puntos, subir dificultad (m√°s r√°pido)
    if (score > 0 && score % 10 === 0 && bpm < 120) {
      const nuevoBpm = bpm + 10;
      setBpm(nuevoBpm);
      setNivel((n) => n + 1);
      iniciarBeatLoop(nuevoBpm);
    }
  };

  // üîπ Barra espaciadora
  useEffect(() => {
    const handleKey = (e) => {
      if (e.code === "Space") {
        e.preventDefault();
        handlePress();
      }
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  });

  return (
    <div className="pulso-container fade-in">
      <h1 className="actividad-titulo">üéµ Pulso Musical</h1>
      <p className="actividad-descripcion">
        Pulsa al ritmo del sonido o la luz (barra espaciadora o bot√≥n).
      </p>

      <div className="info-box">
        <p><strong>Nivel:</strong> {nivel}</p>
        <p><strong>BPM:</strong> {bpm}</p>
        <p><strong>Puntuaci√≥n:</strong> {score}</p>
      </div>

      <div className={`ritmo-circulo ${highlight ? "activo" : ""}`}></div>

      {feedback && <p className="feedback">{feedback}</p>}

      <div className="botones">
        {!playing ? (
          <button className="boton-actividad" onClick={start}>üöÄ Iniciar</button>
        ) : (
          <button className="boton-actividad detener" onClick={stop}>‚èπ Detener</button>
        )}
        <button className="boton-volver" onClick={volver}>‚¨Ö Volver</button>
        <button className="boton-actividad" onClick={handlePress}>üéØ Pulso</button>
      </div>
    </div>
  );
}
