import React, { useState } from "react";
import { sendActivity } from "../../services/activityService";
import "./Social.css";

export default function EmpatiaEnAccion({ volver }) {
  const escenarios = [
    {
      id: 1,
      descripcion: "Tu amigo est√° llorando porque perdi√≥ su juguete favorito.",
      correcta: "Tristeza",
      opciones: ["Felicidad", "Tristeza", "Miedo", "Enojo"],
    },
    {
      id: 2,
      descripcion: "Ganas un juego despu√©s de muchos intentos.",
      correcta: "Alegr√≠a",
      opciones: ["Alegr√≠a", "Tristeza", "Enojo", "Sorpresa"],
    },
    {
      id: 3,
      descripcion: "Un perro grande ladra muy cerca de ti.",
      correcta: "Miedo",
      opciones: ["Felicidad", "Miedo", "Tranquilidad", "Alegr√≠a"],
    },
    {
      id: 4,
      descripcion: "Tu hermano rompe accidentalmente tu dibujo.",
      correcta: "Enojo",
      opciones: ["Tristeza", "Sorpresa", "Enojo", "Alegr√≠a"],
    },
    {
      id: 5,
      descripcion: "Tu mam√° te abraza despu√©s de un d√≠a largo.",
      correcta: "Amor",
      opciones: ["Amor", "Miedo", "Sorpresa", "Tristeza"],
    },
  ];

  const [escenarioActual, setEscenarioActual] = useState(
    escenarios[Math.floor(Math.random() * escenarios.length)]
  );
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);

  // ‚úÖ Nueva funci√≥n para registrar la puntuaci√≥n y enviar actividad
  const onPuntuar = async (puntos = 1) => {
    setScore((s) => s + puntos);
    try {
      await sendActivity({
        modulo: "Social",
        actividad: "Empat√≠a en acci√≥n",
        puntuacion: puntos,
        escenario: escenarioActual.descripcion,
      });
      console.log("‚úÖ Actividad registrada correctamente (Empat√≠aEnAccion)");
    } catch (err) {
      console.warn("‚ö†Ô∏è No se pudo enviar la actividad al servidor:", err);
    }
  };

  const elegirOpcion = (opcion) => {
    if (opcion === escenarioActual.correcta) {
      setFeedback("‚úÖ ¬°Correcto! Reconociste la emoci√≥n.");
      onPuntuar(1);
      setTimeout(() => nuevaRonda(), 2000);
    } else {
      setFeedback("‚ùå Intenta de nuevo, piensa c√≥mo se sentir√≠a esa persona.");
    }
  };

  const nuevaRonda = () => {
    const nueva =
      escenarios[Math.floor(Math.random() * escenarios.length)];
    setEscenarioActual(nueva);
    setFeedback("");
  };

  return (
    <div className="actividad-social">
      <h1>üíû Empat√≠a en acci√≥n</h1>
      <p className="contexto">
        <strong>Situaci√≥n:</strong> {escenarioActual.descripcion}
      </p>
      <p>¬øQu√© emoci√≥n representa mejor esta situaci√≥n?</p>

      <div className="opciones-emociones">
        {escenarioActual.opciones.map((op, i) => (
          <button
            key={i}
            className="opcion-emocion"
            onClick={() => elegirOpcion(op)}
          >
            {op}
          </button>
        ))}
      </div>

      {feedback && <p className="feedback">{feedback}</p>}
      <p className="puntaje">Puntuaci√≥n: {score}</p>

      <button className="boton-volver" onClick={volver}>
        ‚¨Ö Volver
      </button>
    </div>
  );
}
