import React, { useState, useEffect, useRef } from "react";
import "./Sensorial.css";

export default function PulsoMusical({ volver }) {
  const [playing, setPlaying] = useState(false);
  const [score, setScore] = useState(0);
  const [beat, setBeat] = useState(0);
  const [feedback, setFeedback] = useState("");
  const [circleScale, setCircleScale] = useState(1);
  const intervalRef = useRef(null);
  const audioCtxRef = useRef(null);
  const nextBeatTimeRef = useRef(0);

  const playTone = () => {
    if (!audioCtxRef.current) return;
    const ctx = audioCtxRef.current;
    const osc = ctx.createOscillator();
    const gain = ctx.createGain();
    osc.type = "sine";
    osc.frequency.setValueAtTime(440, ctx.currentTime);
    gain.gain.setValueAtTime(0.05, ctx.currentTime);
    osc.connect(gain);
    gain.connect(ctx.destination);
    osc.start();
    osc.stop(ctx.currentTime + 0.15);
  };

  const startGame = () => {
    if (!audioCtxRef.current)
      audioCtxRef.current = new (window.AudioContext || window.webkitAudioContext)();

    setPlaying(true);
    setScore(0);
    setBeat(0);
    setFeedback("");
    nextBeatTimeRef.current = audioCtxRef.current.currentTime + 1;

    intervalRef.current = setInterval(() => {
      setBeat((prev) => prev + 1);
      playTone();
      animateCircle();
    }, 1500);
  };

  const stopGame = () => {
    setPlaying(false);
    clearInterval(intervalRef.current);
  };

  const animateCircle = () => {
    setCircleScale(1.5);
    setTimeout(() => setCircleScale(1), 700);
  };

  const handlePress = () => {
    if (!audioCtxRef.current || !playing) return;
    const now = audioCtxRef.current.currentTime;
    const diff = Math.abs(now - nextBeatTimeRef.current);

    if (diff < 0.5) {
      // Â±0.5 s de tolerancia
      setScore((prev) => prev + 1);
      setFeedback("Â¡Buen ritmo! ðŸ¥");
    } else {
      setFeedback("Fuera de tiempo â±ï¸");
    }
  };

  useEffect(() => {
    if (playing) nextBeatTimeRef.current += 1.5;
  }, [beat, playing]);

  useEffect(() => {
    const handleKey = (e) => {
      if (e.code === "Space") {
        e.preventDefault();
        handlePress();
      }
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  });

  return (
    <div className="pulso-musical">
      <h1 className="actividad-titulo">ðŸŽµ Pulso Musical</h1>
      <p className="actividad-descripcion">
        Presiona el botÃ³n (o barra espaciadora) al ritmo del sonido y la luz.
      </p>

      {!playing ? (
        <button className="boton-actividad" onClick={startGame}>
          Iniciar ritmo
        </button>
      ) : (
        <button className="boton-actividad detener" onClick={stopGame}>
          Detener
        </button>
      )}

      <div
        className="ritmo-circulo"
        style={{
          transform: `scale(${circleScale})`,
          transition: "transform 0.5s ease",
        }}
      ></div>

      {feedback && <p className="feedback">{feedback}</p>}
      <p className="puntaje">PuntuaciÃ³n: {score}</p>

      <button className="boton-volver" onClick={volver}>
        â¬… Volver
      </button>
    </div>
  );
}
