import React, { useState } from "react";
import SinfoniaColores from "./SinfoniaColores";
import "../../styles/SinfoniaColores.css";

export default function Sensorial() {
  const [actividad, setActividad] = useState(null);

  const actividades = [
    {
      name: "Sinfon칤a de Colores",
      description:
        "Repite la secuencia de luces y sonidos para mejorar tu memoria sensorial.",
      difficulty: "F치cil",
      stimulus: "Auditivo / Visual",
    },
    // Aqu칤 luego puedes agregar m치s juegos sensoriales
  ];

  const renderActividad = () => {
    if (actividad === "Sinfon칤a de Colores")
      return <SinfoniaColores volver={() => setActividad(null)} />;
    return null;
  };

  return (
    <div
      style={{
        padding: "24px",
        fontFamily: "Poppins, sans-serif",
        textAlign: "center",
      }}
    >
      <h1>游꿧 M칩dulo Sensorial</h1>

      {actividad ? (
        renderActividad()
      ) : (
        <div>
          <p>Selecciona una actividad para comenzar:</p>
          <ul
            style={{
              listStyle: "none",
              padding: 0,
              display: "grid",
              gap: "12px",
              justifyContent: "center",
            }}
          >
            {actividades.map((a, i) => (
              <li
                key={i}
                onClick={() => setActividad(a.name)}
                style={{
                  background: "#f6f8fa",
                  borderRadius: "12px",
                  padding: "16px 24px",
                  width: "320px",
                  cursor: "pointer",
                  boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                  transition: "transform 0.15s ease",
                }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.transform = "scale(1.03)")
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.transform = "scale(1)")
                }
              >
                <strong>{a.name}</strong>
                <p style={{ fontSize: "14px", margin: "6px 0" }}>
                  {a.description}
                </p>
                <small style={{ color: "#666" }}>
                  游꿢 Dificultad: {a.difficulty} | Est칤mulo: {a.stimulus}
                </small>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
