import React, { useState, useEffect, useRef } from "react";
import "./PulsoMusical.css";

export default function PulsoMusical({ volver }) {
  const [playing, setPlaying] = useState(false);
  const [score, setScore] = useState(0);
  const [level, setLevel] = useState(1);
  const [feedback, setFeedback] = useState("");
  const [position, setPosition] = useState(0);
  const [direction, setDirection] = useState(1);
  const [speed, setSpeed] = useState(0.5);
  const [fails, setFails] = useState(0);

  const audioCtxRef = useRef(null);
  const animRef = useRef(null);

  // reproducir sonido con frecuencia distinta seg√∫n acierto o fallo
  const playTone = (freq = 440) => {
    const ctx = audioCtxRef.current;
    const osc = ctx.createOscillator();
    const gain = ctx.createGain();
    osc.type = "sine";
    osc.frequency.setValueAtTime(freq, ctx.currentTime);
    gain.gain.setValueAtTime(0.07, ctx.currentTime);
    osc.connect(gain);
    gain.connect(ctx.destination);
    osc.start();
    osc.stop(ctx.currentTime + 0.2);
  };

  // animaci√≥n suave
  const animate = () => {
    setPosition((p) => {
      let newPos = p + direction * speed;
      if (newPos >= 100 || newPos <= 0) setDirection((d) => -d);
      return Math.max(0, Math.min(100, newPos));
    });
    animRef.current = requestAnimationFrame(animate);
  };

  const start = () => {
    if (!audioCtxRef.current)
      audioCtxRef.current = new (window.AudioContext || window.webkitAudioContext)();

    setPlaying(true);
    setScore(0);
    setFails(0);
    setLevel(1);
    setSpeed(0.5);
    setFeedback("¬°Comienza el ritmo! ü•Å");
    cancelAnimationFrame(animRef.current);
    animate();
  };

  const stop = (msg = "Juego detenido") => {
    setPlaying(false);
    cancelAnimationFrame(animRef.current);
    setFeedback(msg);
  };

  const handlePress = () => {
    if (!playing) return;
    const diff = Math.abs(position - 50);
    if (diff <= 10) {
      setScore((s) => s + 1);
      playTone(700);
      setFeedback("‚ú® ¬°Buen ritmo!");
    } else {
      setFails((f) => f + 1);
      playTone(200);
      setFeedback("‚ùå Fuera de tiempo");
    }
  };

  // progresi√≥n y reinicio autom√°tico
  useEffect(() => {
    if (score > 0 && score % 5 === 0) {
      setLevel((l) => l + 1);
      setSpeed((s) => Math.min(s + 0.2, 2));
      setFeedback(`üé∂ Nivel ${level + 1}!`);
    }
  }, [score]);

  useEffect(() => {
    if (fails >= 3) {
      stop("üòî Reiniciando...");
      setTimeout(() => start(), 2000);
    }
  }, [fails]);

  // control con barra espaciadora
  useEffect(() => {
    const handleKey = (e) => {
      if (e.code === "Space") {
        e.preventDefault();
        handlePress();
      }
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  });

  return (
    <div className="pulso-musical">
      <h1 className="actividad-titulo">üéµ Pulso Musical</h1>
      <p className="actividad-descripcion">
        Presiona cuando la luz est√© en la zona verde.
      </p>

      {!playing ? (
        <button className="boton-actividad" onClick={start}>
          Iniciar
        </button>
      ) : (
        <button className="boton-actividad detener" onClick={() => stop("Detenido manualmente")}>
          Detener
        </button>
      )}

      <div className="barra-contenedor">
        <div className="zona-verde"></div>
        <div className="indicador" style={{ left: `${position}%` }}></div>
      </div>

      <p className="feedback">{feedback}</p>
      <p className="puntaje">‚úÖ {score} | ‚ùå {fails} | Nivel {level}</p>

      <button className="boton-presionar" onClick={handlePress}>
        Presionar
      </button>

      <button className="boton-volver" onClick={volver}>
        ‚¨Ö Volver
      </button>
    </div>
  );
}
