import React, { useState, useEffect } from "react";
import "../../styles/Sensorial.css";

export default function LightChase({ volver }) {
  const [score, setScore] = useState(0);
  const [lights, setLights] = useState([]);
  const [gameActive, setGameActive] = useState(false);
  const [level, setLevel] = useState(1);
  const [message, setMessage] = useState("Haz clic en 'Comenzar' para iniciar");

  const initialInterval = 2000; // 2 segundos entre luces al inicio
  const minInterval = 800; // velocidad mínima
  const [intervalTime, setIntervalTime] = useState(initialInterval);

  // 🟣 Duración que una luz permanece visible
  const lightDuration = 1800;

  // Generar luces
  useEffect(() => {
    if (!gameActive) return;

    const interval = setInterval(() => {
      const newLight = {
        id: Date.now(),
        x: Math.random() * 80 + 10,
        y: Math.random() * 70 + 10,
        color: getRandomColor(),
      };
      setLights((prev) => [...prev, newLight]);

      // La luz desaparece tras un tiempo
      setTimeout(() => {
        setLights((prev) => prev.filter((l) => l.id !== newLight.id));
      }, lightDuration);

      // Aumentar dificultad cada 5 aciertos
      if (score > 0 && score % 5 === 0 && intervalTime > minInterval) {
        setLevel((lvl) => lvl + 1);
        setIntervalTime((prev) => prev - 150);
        setMessage(`Nivel ${level + 1}: ¡Más rápido!`);
      }
    }, intervalTime);

    return () => clearInterval(interval);
  }, [gameActive, score, intervalTime, level]);

  // 🎨 Colores aleatorios bonitos
  const getRandomColor = () => {
    const colors = ["#ff99c8", "#fcf6bd", "#d0f4de", "#a9def9", "#e4c1f9"];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  // Clic en luz
  const handleLightClick = (id) => {
    setLights((prev) => prev.filter((l) => l.id !== id));
    setScore((prev) => prev + 1);
    setMessage("¡Excelente reflejo!");
  };

  // Iniciar o reiniciar
  const startGame = () => {
    setScore(0);
    setLevel(1);
    setIntervalTime(initialInterval);
    setLights([]);
    setMessage("¡Empieza la caza de luces!");
    setGameActive(true);
  };

  // Detener
  const stopGame = () => {
    setGameActive(false);
    setMessage("Juego terminado. ¡Buen trabajo!");
  };

  return (
    <div className="lightchase-container fade-in">
      <h2 className="sensorial-title">✨ Caza de Luz</h2>
      <p className="sensorial-descripcion">
        Haz clic en las luces que aparecen antes de que desaparezcan. Al inicio
        se moverán despacio, pero con el tiempo ¡irán más rápido!
      </p>

      {!gameActive ? (
        <button className="sensorial-boton" onClick={startGame}>
          🚀 Comenzar
        </button>
      ) : (
        <button className="sensorial-boton" onClick={stopGame}>
          ⏹️ Detener
        </button>
      )}

      <div className="lightchase-score">
        <p>Puntuación: {score}</p>
        <p>Nivel: {level}</p>
        <p>{message}</p>
      </div>

      {/* Luces interactivas */}
      <div className="lightchase-playzone">
        {lights.map((light) => (
          <div
            key={light.id}
            className="light-dot"
            style={{
              top: `${light.y}%`,
              left: `${light.x}%`,
              background: light.color,
            }}
            onClick={() => handleLightClick(light.id)}
          ></div>
        ))}
      </div>

      <button className="boton-volver" onClick={volver}>
        ← Volver al módulo sensorial
      </button>
    </div>
  );
}
