import React, { useState, useEffect, useRef } from "react";
import "./LuzRebote.css";

export default function LuzRebote() {
  const [pos, setPos] = useState({ x: 100, y: 100 });
  const [dir, setDir] = useState({ dx: 2, dy: 1.5 });
  const [score, setScore] = useState(0);
  const [errores, setErrores] = useState(0);
  const areaRef = useRef(null);
  const luzRef = useRef(null);

  useEffect(() => {
    const move = () => {
      const area = areaRef.current.getBoundingClientRect();
      const luz = luzRef.current.getBoundingClientRect();

      let newX = pos.x + dir.dx;
      let newY = pos.y + dir.dy;
      let newDx = dir.dx;
      let newDy = dir.dy;

      // Rebotes contra los bordes
      if (newX <= 0 || newX + luz.width >= area.width) newDx = -newDx;
      if (newY <= 0 || newY + luz.height >= area.height) newDy = -newDy;

      setPos({ x: Math.min(Math.max(newX, 0), area.width - luz.width), y: Math.min(Math.max(newY, 0), area.height - luz.height) });
      setDir({ dx: newDx, dy: newDy });
    };

    const interval = setInterval(move, 20); // m치s lento = m치s manejable
    return () => clearInterval(interval);
  }, [pos, dir]);

  const handleClick = () => {
    setScore(score + 1);
  };

  const handleAreaClick = (e) => {
    if (e.target.id !== "luz") setErrores(errores + 1);
  };

  return (
    <div className="container">
      <div className="info">
        <p>Puntaje: {score}</p>
        <p>Errores: {errores}</p>
      </div>
      <div ref={areaRef} className="area" onClick={handleAreaClick}>
        <div
          ref={luzRef}
          id="luz"
          className="luz"
          style={{ transform: `translate(${pos.x}px, ${pos.y}px)` }}
          onClick={handleClick}
        />
      </div>
    </div>
  );
}
