// src/modules/Sensorial/SinfoniaColores.jsx
import React, { useState, useEffect } from "react";
import "../../styles/SinfoniaColores.css";

export default function SinfoniaColores({ volver }) {
  const [secuencia, setSecuencia] = useState([]);
  const [usuarioSecuencia, setUsuarioSecuencia] = useState([]);
  const [nivel, setNivel] = useState(1);
  const [jugando, setJugando] = useState(false);
  const [mensaje, setMensaje] = useState("Presiona 'Comenzar' para jugar üéµ");

  const colores = ["rojo", "azul", "verde", "amarillo"];
  const sonidos = {
    rojo: new Audio("https://actions.google.com/sounds/v1/cartoon/clang_and_wobble.ogg"),
    azul: new Audio("https://actions.google.com/sounds/v1/cartoon/wood_plank_flicks.ogg"),
    verde: new Audio("https://actions.google.com/sounds/v1/cartoon/slide_whistle.ogg"),
    amarillo: new Audio("https://actions.google.com/sounds/v1/cartoon/boing.ogg"),
  };

  // üß© Generar una secuencia aleatoria
  const generarSecuencia = (n) => {
    const nuevaSecuencia = Array.from({ length: n }, () => colores[Math.floor(Math.random() * colores.length)]);
    setSecuencia(nuevaSecuencia);
    setUsuarioSecuencia([]);
    mostrarSecuencia(nuevaSecuencia);
  };

  // üîä Mostrar luces y sonidos secuencialmente
  const mostrarSecuencia = async (seq) => {
    setJugando(true);
    setMensaje("Observa y escucha la secuencia...");
    for (let i = 0; i < seq.length; i++) {
      await new Promise((res) => setTimeout(res, 800));
      reproducirColor(seq[i]);
    }
    setJugando(false);
    setMensaje("¬°Tu turno! Repite la secuencia üé®");
  };

  // üé® Reproducir color con sonido
  const reproducirColor = (color) => {
    const el = document.getElementById(color);
    if (el) {
      el.classList.add("activo");
      sonidos[color].play();
      setTimeout(() => el.classList.remove("activo"), 400);
    }
  };

  // üß† Validar elecci√≥n del usuario
  const manejarClick = (color) => {
    if (jugando) return;

    const nuevaSecuencia = [...usuarioSecuencia, color];
    setUsuarioSecuencia(nuevaSecuencia);
    reproducirColor(color);

    if (color !== secuencia[nuevaSecuencia.length - 1]) {
      setMensaje("‚ùå Oops, intenta de nuevo desde el inicio");
      setNivel(1);
      setSecuencia([]);
      return;
    }

    if (nuevaSecuencia.length === secuencia.length) {
      setMensaje("‚úÖ ¬°Bien hecho! Pasas al siguiente nivel");
      setNivel(nivel + 1);
      setTimeout(() => generarSecuencia(nivel + 1), 1000);
    }
  };

  return (
    <div className="sinfonia-container">
      <h2>üé∂ Sinfon√≠a de Colores</h2>
      <p>{mensaje}</p>
      <div className="cuadro-container">
        {colores.map((color) => (
          <button
            key={color}
            id={color}
            className={`cuadro ${color}`}
            onClick={() => manejarClick(color)}
            disabled={jugando}
          />
        ))}
      </div>
      <p>Nivel actual: {nivel}</p>
      <div className="botones">
        <button
          onClick={() => generarSecuencia(nivel)}
          className="btn-jugar"
        >
          üéÆ Comenzar
        </button>
        <button onClick={volver} className="btn-volver">
          ‚Üê Volver
        </button>
      </div>
    </div>
  );
}
