import React, { useState, useEffect, useRef } from "react";
import "./PulsoMusical.css";

export default function PulsoMusical({ volver }) {
  const [playing, setPlaying] = useState(false);
  const [score, setScore] = useState(0);
  const [level, setLevel] = useState(1);
  const [feedback, setFeedback] = useState("");
  const [position, setPosition] = useState(0);
  const [speed, setSpeed] = useState(0.25); // velocidad (porcentaje por frame-ish)
  const [fails, setFails] = useState(0);

  const audioCtxRef = useRef(null);
  const animRef = useRef(null);

  // refs para controlar física sin side-effects en setState
  const posRef = useRef(0);
  const dirRef = useRef(1); // 1 => derecha, -1 => izquierda
  const lastBounceRef = useRef(null); // "left" | "right" | null -> evita beeps repetidos
  const lastCrossRef = useRef(false); // evita beep central repetido si queda oscilando en 50

  const ensureAudio = () => {
    if (!audioCtxRef.current)
      audioCtxRef.current = new (window.AudioContext || window.webkitAudioContext)();
  };

  const playTone = (freq = 440, len = 0.12) => {
    ensureAudio();
    try {
      const ctx = audioCtxRef.current;
      const osc = ctx.createOscillator();
      const gain = ctx.createGain();
      osc.type = "sine";
      osc.frequency.setValueAtTime(freq, ctx.currentTime);
      gain.gain.setValueAtTime(0.08, ctx.currentTime);
      osc.connect(gain);
      gain.connect(ctx.destination);
      osc.start();
      osc.stop(ctx.currentTime + len);
    } catch (e) {
      // fallbacks silenciosos (previene errores en navegadores bloqueando audio)
    }
  };

  // loop de animación basado en refs para evitar side-effects repetidos
  const animate = () => {
    // si no está jugando, no seguir
    if (!playing) {
      return;
    }

    // avance
    let prev = posRef.current;
    let next = prev + dirRef.current * speed;

    // manejo de rebote en extremos (0..100)
    if (next >= 100) {
      next = 100;
      if (dirRef.current > 0) {
        dirRef.current = -Math.abs(dirRef.current);
        if (lastBounceRef.current !== "right") {
          lastBounceRef.current = "right";
          playTone(520, 0.08); // beep de rebote (una vez)
        }
      }
    } else if (next <= 0) {
      next = 0;
      if (dirRef.current < 0) {
        dirRef.current = Math.abs(dirRef.current);
        if (lastBounceRef.current !== "left") {
          lastBounceRef.current = "left";
          playTone(520, 0.08);
        }
      }
    } else {
      // estamos en movimiento interior, resetear flag de bounce
      lastBounceRef.current = null;
    }

    // beep central: solo al cruzar el centro (prev < 50 <= next) o (prev > 50 >= next)
    const crossed =
      (prev < 50 && next >= 50) || (prev > 50 && next <= 50);
    if (crossed && !lastCrossRef.current) {
      lastCrossRef.current = true;
      playTone(440, 0.09);
    } else if (!crossed) {
      lastCrossRef.current = false;
    }

    // aplicar
    posRef.current = next;
    setPosition(Number(next.toFixed(3)));

    animRef.current = requestAnimationFrame(animate);
  };

  const start = () => {
    ensureAudio();
    cancelAnimationFrame(animRef.current);
    // reset refs y estados
    posRef.current = 0;
    dirRef.current = 1;
    lastBounceRef.current = null;
    lastCrossRef.current = false;

    setPosition(0);
    setScore(0);
    setFails(0);
    setLevel(1);
    setSpeed(0.25);
    setFeedback("🎶 ¡Sigue el ritmo!");
    setPlaying(true);
    animRef.current = requestAnimationFrame(animate);
  };

  const stop = (msg = "Detenido") => {
    setPlaying(false);
    cancelAnimationFrame(animRef.current);
    setFeedback(msg);
  };

  const handlePress = () => {
    if (!playing) return;
    const diff = Math.abs(posRef.current - 50);
    if (diff <= 10) {
      setScore((s) => s + 1);
      playTone(700, 0.12);
      setFeedback("✨ ¡Buen ritmo!");
    } else {
      setFails((f) => f + 1);
      playTone(200, 0.12);
      setFeedback("❌ Fuera de tiempo");
    }
  };

  // progresión suave
  useEffect(() => {
    if (score > 0 && score % 5 === 0) {
      setLevel((l) => l + 1);
      setSpeed((s) => Math.min(s + 0.05, 1.0));
      setFeedback((_) => `🎵 Nivel ${level + 1}`);
    }
  }, [score]);

  useEffect(() => {
    if (fails >= 3) {
      stop("😔 Reiniciando...");
      setTimeout(() => start(), 1800);
    }
  }, [fails]);

  // barra espaciadora
  useEffect(() => {
    const handleKey = (e) => {
      if (e.code === "Space") {
        e.preventDefault();
        handlePress();
      }
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [playing]);

  // cleanup cuando desmonta
  useEffect(() => {
    return () => {
      cancelAnimationFrame(animRef.current);
      if (audioCtxRef.current && audioCtxRef.current.state === "running") {
        // no cerramos el audioCtx para evitar problemas, solo lo dejamos
      }
    };
  }, []);

  return (
    <div className="pulso-musical">
      <h1 className="actividad-titulo">🎵 Pulso Musical</h1>
      <p className="actividad-descripcion">Presiona cuando la luz esté en la zona verde.</p>

      {!playing ? (
        <button className="boton-actividad" onClick={start}>
          Iniciar
        </button>
      ) : (
        <button className="boton-actividad detener" onClick={() => stop("Detenido manualmente")}>
          Detener
        </button>
      )}

      <div className="barra-contenedor">
        <div className="zona-verde"></div>
        <div className="indicador" style={{ left: `${position}%` }}></div>
      </div>

      <p className="feedback">{feedback}</p>
      <p className="puntaje">✅ {score} | ❌ {fails} | Nivel {level}</p>

      <button className="boton-presionar" onClick={handlePress}>
        Presionar
      </button>

      <button className="boton-volver" onClick={volver}>
        ⬅ Volver
      </button>
    </div>
  );
}
