import React, { useState } from "react";
import "./ExploraPorQue.css";

const PREGUNTAS = {
  facil: [
    {
      id: 1,
      question: "¬øPor qu√© usamos paraguas cuando llueve?",
      options: [
        "Porque nos protege del agua",
        "Porque es bonito",
        "Porque ayuda a que llueva m√°s",
        "Porque hace ruido"
      ],
      correct: 0,
      explanation: "El paraguas sirve para cubrirnos y mantenernos secos cuando llueve."
    },
    {
      id: 2,
      question: "¬øPor qu√© debemos dormir por la noche?",
      options: [
        "Porque el cuerpo necesita descansar y recuperar energ√≠a",
        "Porque la luna nos duerme",
        "Porque el sol se va",
        "Porque as√≠ lo dice mam√°"
      ],
      correct: 0,
      explanation: "Dormir ayuda al cuerpo y al cerebro a descansar y estar listos para el d√≠a siguiente."
    },
    {
      id: 3,
      question: "¬øPor qu√© comemos frutas y verduras?",
      options: [
        "Porque tienen vitaminas que nos mantienen sanos",
        "Porque son de colores bonitos",
        "Porque crecen r√°pido",
        "Porque son dulces"
      ],
      correct: 0,
      explanation: "Las frutas y verduras tienen nutrientes y vitaminas que fortalecen el cuerpo."
    }
  ],

  normal: [
    {
      id: 1,
      question: "¬øPor qu√© el hielo se derrite al sol?",
      options: [
        "Porque el sol lo calienta y cambia su estado a l√≠quido",
        "Porque el viento lo rompe",
        "Porque el agua quiere salir del hielo",
        "Porque se cansa de estar fr√≠o"
      ],
      correct: 0,
      explanation:
        "El calor del sol aumenta la temperatura del hielo, lo que hace que cambie de estado s√≥lido a l√≠quido."
    },
    {
      id: 2,
      question: "¬øPor qu√© las plantas necesitan luz del sol?",
      options: [
        "Porque el sol les da energ√≠a para producir su alimento",
        "Porque el sol las pinta de verde",
        "Porque el sol las calienta",
        "Porque las hace felices"
      ],
      correct: 0,
      explanation:
        "Las plantas usan la luz del sol para hacer fotos√≠ntesis y producir su propio alimento."
    },
    {
      id: 3,
      question: "¬øPor qu√© debemos lavarnos las manos antes de comer?",
      options: [
        "Porque as√≠ quitamos g√©rmenes y suciedad",
        "Porque el agua es sabrosa",
        "Porque nos gusta el jab√≥n",
        "Porque el agua enfr√≠a las manos"
      ],
      correct: 0,
      explanation:
        "Lavarse las manos elimina bacterias y virus que podr√≠an enfermarnos al comer."
    }
  ],

  dificil: [
    {
      id: 1,
      question: "¬øPor qu√© el cielo se ve azul durante el d√≠a?",
      options: [
        "Porque el aire dispersa la luz azul del sol m√°s que otros colores",
        "Porque el mar refleja su color",
        "Porque el sol pinta el cielo",
        "Porque el azul es el color m√°s fr√≠o"
      ],
      correct: 0,
      explanation:
        "Las part√≠culas del aire dispersan m√°s la luz azul del sol que otros colores, haciendo que el cielo se vea azul."
    },
    {
      id: 2,
      question: "¬øPor qu√© los metales se calientan m√°s r√°pido al sol?",
      options: [
        "Porque absorben mejor el calor",
        "Porque son m√°s pesados",
        "Porque son de color gris",
        "Porque el sol los elige"
      ],
      correct: 0,
      explanation:
        "Los metales conducen y absorben el calor con facilidad, por eso se calientan m√°s r√°pido."
    },
    {
      id: 3,
      question: "¬øPor qu√© los barcos flotan aunque son pesados?",
      options: [
        "Porque su forma reparte el peso sobre el agua",
        "Porque el agua los empuja hacia arriba m√°gicamente",
        "Porque tienen aire dentro",
        "Porque son de color claro"
      ],
      correct: 0,
      explanation:
        "La forma del barco permite que el peso se distribuya sobre el agua, generando flotaci√≥n."
    }
  ]
};

export default function ExploraPorQue({ volver, dificultad = "normal" }) {
  const preguntas = PREGUNTAS[dificultad] || PREGUNTAS.normal;
  const [current, setCurrent] = useState(0);
  const [selected, setSelected] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [showExplanation, setShowExplanation] = useState(false);

  const pregunta = preguntas[current];

  const handleAnswer = (index) => {
    setSelected(index);
    const correct = index === pregunta.correct;
    const msg = correct
      ? "¬°Excelente! Respuesta correcta üéâ"
      : "No es correcto, intenta de nuevo.";
    setFeedback(msg);
    setShowExplanation(correct);

    if (correct) {
      const voice = new SpeechSynthesisUtterance("¬°Muy bien! Respuesta correcta.");
      voice.lang = "es-ES";
      window.speechSynthesis.speak(voice);
    }
  };

  const nextQuestion = () => {
    setSelected(null);
    setFeedback("");
    setShowExplanation(false);
    setCurrent((prev) => (prev + 1) % preguntas.length);
  };

  return (
    <div className="explora-container">
      <h2>üîç Explora el Porqu√©</h2>
      <p className="explora-instruccion">Lee la pregunta y elige la mejor respuesta.</p>

      <div className="explora-card">
        <h3>{pregunta.question}</h3>

        <div className="explora-options">
          {pregunta.options.map((opt, i) => (
            <button
              key={i}
              onClick={() => handleAnswer(i)}
              className={`explora-btn ${
                selected === i
                  ? i === pregunta.correct
                    ? "correct"
                    : "incorrect"
                  : ""
              }`}
              disabled={showExplanation}
            >
              {opt}
            </button>
          ))}
        </div>

        <p className="explora-feedback">{feedback}</p>

        {showExplanation && (
          <div className="explora-explanation">
            <strong>üß† ¬øSab√≠as que...? </strong>
            <p>{pregunta.explanation}</p>
            <button onClick={nextQuestion}>‚û°Ô∏è Siguiente pregunta</button>
          </div>
        )}
      </div>

      {volver && (
        <button className="volver-btn" onClick={volver}>
          ‚Üê Volver
        </button>
      )}
    </div>
  );
}
