import React, { useState, useEffect, useRef } from "react";
import "./LightChase.css"
export default function CazaDeLuz({ volver }) {
  const [jugando, setJugando] = useState(false);
  const [nivel, setNivel] = useState(1);
  const [puntuacion, setPuntuacion] = useState(0);
  const [objetivo, setObjetivo] = useState(5);
  const [luces, setLuces] = useState([]);
  const [mensaje, setMensaje] = useState("");
  const [intervalo, setIntervalo] = useState(1500);
  const areaJuego = useRef(null);
  const timeoutRef = useRef(null);

  // generar luces seg√∫n nivel
  useEffect(() => {
    if (!jugando) return;
    const interval = setInterval(() => generarLuz(), intervalo);
    timeoutRef.current = setTimeout(() => perderNivel(), 15000);

    return () => {
      clearInterval(interval);
      clearTimeout(timeoutRef.current);
    };
  }, [jugando, intervalo]);

  const generarLuz = () => {
    if (!areaJuego.current) return;
    const { width, height } = areaJuego.current.getBoundingClientRect();
    const x = Math.random() * (width - 40);
    const y = Math.random() * (height - 40);
    const id = Date.now();
    setLuces((prev) => [...prev, { id, x, y }]);
    setTimeout(() => {
      setLuces((prev) => prev.filter((l) => l.id !== id));
    }, 1000);
  };

  const handleClick = (id) => {
    setPuntuacion((p) => p + 1);
    setLuces((prev) => prev.filter((l) => l.id !== id));
  };

  const perderNivel = () => {
    setMensaje("üò¢ Se acab√≥ el tiempo. Intenta de nuevo.");
    setJugando(false);
    setLuces([]);
  };

  const ganarNivel = () => {
    setMensaje("üéâ ¬°Nivel completado!");
    setJugando(false);
    setNivel((n) => n + 1);
    setIntervalo((i) => Math.max(800, i - 200));
    setObjetivo((o) => o + 3);
  };

  useEffect(() => {
    if (jugando && puntuacion >= objetivo) ganarNivel();
  }, [puntuacion]);

  const iniciar = () => {
    setJugando(true);
    setPuntuacion(0);
    setMensaje("");
    setLuces([]);
  };

  return (
    <div className="cazaluz-container fade-in">
      <h2>‚ú® Caza de Luz</h2>
      <p>Haz clic en {objetivo} luces antes de que acabe el tiempo.</p>

      <div className="cazaluz-area" ref={areaJuego}>
        {luces.map((l) => (
          <div
            key={l.id}
            className="cazaluz-light"
            style={{ top: l.y, left: l.x }}
            onClick={() => handleClick(l.id)}
          ></div>
        ))}
      </div>

      <div className="cazaluz-info">
        <p>Nivel: {nivel}</p>
        <p>Puntuaci√≥n: {puntuacion}/{objetivo}</p>
        <p className="mensaje">{mensaje}</p>

        {!jugando ? (
          <button onClick={iniciar}>üöÄ {mensaje ? "Reintentar" : "Comenzar"}</button>
        ) : (
          <button onClick={() => perderNivel()}>‚èπ Detener</button>
        )}
        <button className="volver" onClick={volver}>‚Üê Volver</button>
      </div>
    </div>
  );
}
