import React, { useEffect, useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "./Dashboard.css";

export default function Dashboard() {
  // Datos simulados por ahora (luego se reemplazan por API)
  const [userData, setUserData] = useState({
    name: "Zabdiel",
    skills: {
      Cognitivo: 0.78,
      Sensorial: 0.64,
      Social: 0.52,
    },
    progress_history: [
      { date: "2025-10-01", Cognitivo: 0.65, Sensorial: 0.50, Social: 0.40 },
      { date: "2025-10-05", Cognitivo: 0.72, Sensorial: 0.58, Social: 0.48 },
      { date: "2025-10-08", Cognitivo: 0.78, Sensorial: 0.64, Social: 0.52 },
    ],
  });

  // Ejemplo: luego puedes hacer fetch a tu endpoint FastAPI
  useEffect(() => {
    // fetch("http://127.0.0.1:8000/api/stats/1")
    //   .then(res => res.json())
    //   .then(data => setUserData(data));
  }, []);

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">ðŸ‘‹ Bienvenido, {userData.name}</h1>
      <p className="dashboard-subtitle">Tu progreso general en Cognia</p>

      {/* Bloque de habilidades */}
      <div className="skills-section">
        {Object.entries(userData.skills).map(([skill, value]) => (
          <div key={skill} className="skill-bar">
            <span className="skill-name">{skill}</span>
            <div className="progress-container">
              <div
                className="progress-bar"
                style={{ width: `${value * 100}%` }}
              ></div>
            </div>
            <span className="progress-percent">{Math.round(value * 100)}%</span>
          </div>
        ))}
      </div>

      {/* GrÃ¡fica de progreso */}
      <div className="chart-section">
        <h2>ðŸ“ˆ EvoluciÃ³n de tu rendimiento</h2>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={userData.progress_history}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis domain={[0, 1]} tickFormatter={(v) => `${v * 100}%`} />
            <Tooltip formatter={(v) => `${Math.round(v * 100)}%`} />
            <Legend />
            <Line
              type="monotone"
              dataKey="Cognitivo"
              stroke="#204ac8"
              strokeWidth={3}
              activeDot={{ r: 6 }}
            />
            <Line
              type="monotone"
              dataKey="Sensorial"
              stroke="#3bb273"
              strokeWidth={3}
            />
            <Line
              type="monotone"
              dataKey="Social"
              stroke="#f4a261"
              strokeWidth={3}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>

      {/* MÃ³dulos disponibles */}
      <div className="modules-section">
        <h2>ðŸ§© MÃ³dulos disponibles</h2>
        <div className="modules-grid">
          {["Cognitivo", "Sensorial", "Social", "TÃ©cnico", "Familiar"].map(
            (mod) => (
              <button key={mod} className="module-card">
                {mod}
              </button>
            )
          )}
        </div>
      </div>
    </div>
  );
}
