import React, { useState, useEffect, useRef } from "react";
import "../../styles/Sensorial.css";

export default function CazaDeLuz({ volver }) {
  const [jugando, setJugando] = useState(false);
  const [nivel, setNivel] = useState(1);
  const [puntuacion, setPuntuacion] = useState(0);
  const [objetivo, setObjetivo] = useState(5);
  const [fallos, setFallos] = useState(0);
  const [mensaje, setMensaje] = useState("");
  const [luces, setLuces] = useState([]);
  const [intervalo, setIntervalo] = useState(2000);
  const areaJuego = useRef(null);
  const temporizador = useRef(null);

  useEffect(() => {
    if (!jugando) return;
    const interval = setInterval(generarLuz, intervalo);
    temporizador.current = setTimeout(perderNivel, 15000);

    return () => {
      clearInterval(interval);
      clearTimeout(temporizador.current);
    };
  }, [jugando, intervalo]);

  const generarLuz = () => {
    if (!areaJuego.current) return;
    const { width, height } = areaJuego.current.getBoundingClientRect();
    const x = Math.random() * (width - 40);
    const y = Math.random() * (height - 40);
    const id = Date.now();
    setLuces((prev) => [...prev, { id, x, y }]);

    setTimeout(() => {
      setLuces((prev) => {
        const stillHere = prev.find((l) => l.id === id);
        if (stillHere) setFallos((f) => f + 1);
        return prev.filter((l) => l.id !== id);
      });
    }, 1000);
  };

  const handleClick = (id) => {
    setPuntuacion((p) => p + 1);
    setLuces((prev) => prev.filter((l) => l.id !== id));
  };

  const perderNivel = () => {
    setMensaje("‚ùå Tiempo agotado o demasiados fallos.");
    setJugando(false);
    setLuces([]);
  };

  const ganarNivel = () => {
    setMensaje("üéâ ¬°Nivel completado!");
    setJugando(false);
    setNivel((n) => n + 1);
    setIntervalo((i) => Math.max(800, i - 200));
    setObjetivo((o) => o + 3);
    setFallos(0);
  };

  useEffect(() => {
    if (jugando && puntuacion >= objetivo) ganarNivel();
    if (jugando && fallos >= 3) perderNivel();
  }, [puntuacion, fallos]);

  const iniciar = () => {
    setJugando(true);
    setPuntuacion(0);
    setFallos(0);
    setMensaje("");
    setLuces([]);
  };

  return (
    <div className="sensorial-container fade-in">
      <h1 className="sensorial-title">‚ú® Caza de Luz</h1>
      <p>Haz clic en {objetivo} luces antes de que acabe el tiempo (m√°x 3 fallos).</p>

      <div className="sensorial-game-area" ref={areaJuego}>
        {luces.map((l) => (
          <div
            key={l.id}
            className="sensorial-light"
            style={{ top: l.y, left: l.x }}
            onClick={() => handleClick(l.id)}
          ></div>
        ))}
      </div>

      <div className="sensorial-controls">
        <p>Nivel: {nivel}</p>
        <p>
          Puntos: {puntuacion}/{objetivo} | Fallos: {fallos}/3
        </p>
        <p className="sensorial-message">{mensaje}</p>

        {!jugando ? (
          <button className="sensorial-button" onClick={iniciar}>
            üöÄ {mensaje ? "Reintentar" : "Comenzar"}
          </button>
        ) : (
          <button className="sensorial-button detener" onClick={perderNivel}>
            ‚èπ Detener
          </button>
        )}
        <button className="sensorial-volver" onClick={volver}>
          ‚Üê Volver
        </button>
      </div>
    </div>
  );
}
