import React, { useState } from "react";
import { getActividadesPorModulo } from "../../services/activityService";
import "./sytles/Familiar.css";

export default function Familiar() {
  const actividades = getActividadesPorModulo("Familiar");
  const [actividadSeleccionada, setActividadSeleccionada] = useState(null);

  const renderActividad = () => {
    if (!actividadSeleccionada) return null;

    return (
      <div className="actividad-familiar">
        <h2>{actividadSeleccionada.name}</h2>
        <p className="actividad-descripcion">{actividadSeleccionada.description}</p>

        {/* üîπ Ejemplo simple de actividad interactiva */}
        <div className="zona-interactiva">
          <p>Selecciona la respuesta correcta üëá</p>
          <div className="opciones">
            <button
              className="opcion"
              onClick={() => alert("‚ú® ¬°Muy bien! As√≠ expresas empat√≠a familiar. ‚ù§Ô∏è")}
            >
              "¬øTe sientes bien? Puedo ayudarte üòä"
            </button>
            <button
              className="opcion"
              onClick={() => alert("üôÉ Intenta de nuevo, piensa c√≥mo apoyar a tu familia.")}
            >
              "No es mi problema üòê"
            </button>
          </div>
        </div>

        <button
          className="boton-volver"
          onClick={() => setActividadSeleccionada(null)}
        >
          ‚Üê Volver al m√≥dulo
        </button>
      </div>
    );
  };

  return (
    <div className="modulo-familiar">
      <h1 className="titulo-familiar">üë®‚Äçüë©‚Äçüëß‚Äçüë¶ M√≥dulo Familiar</h1>
      <p className="subtitulo-familiar">
        Fortalece la comunicaci√≥n y empat√≠a con tu familia a trav√©s de actividades simples y visuales üíï
      </p>

      {actividadSeleccionada ? (
        renderActividad()
      ) : (
        <div className="lista-actividades">
          {actividades.map((a, index) => (
            <div
              key={index}
              className="tarjeta-actividad"
              onClick={() => setActividadSeleccionada(a)}
            >
              <h3>{a.name}</h3>
              <p>{a.description}</p>
              <small>üéØ {a.why_useful}</small>
              <p className="etiquetas">
                Dificultad: {a.difficulty} | Est√≠mulo: {a.stimulus}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
