import React from "react";
import "./Social.css";

export default function Social({ seleccionarActividad }) {
  const actividades = [
    {
      titulo: "Construye la respuesta",
      descripcion:
        "El usuario arma una respuesta social adecuada tocando las palabras en orden.",
      objetivo:
        "🎯 Desarrolla el lenguaje expresivo y la estructura de la conversación.",
      dificultad: "3",
      estimulo: "visual",
    },
    {
      titulo: "Voz y emoción",
      descripcion:
        "Escucha una frase con una emoción y selecciona cuál es la emoción expresada.",
      objetivo:
        "🎯 Entrena la interpretación del tono de voz y la empatía auditiva.",
      dificultad: "2",
      estimulo: "auditivo",
    },
    {
      titulo: "Reflejo social",
      descripcion:
        "Un usuario realiza un gesto o movimiento, y su compañero debe imitarlo.",
      objetivo:
        "🎯 Mejora la atención conjunta y la imitación social, claves en la empatía corporal.",
      dificultad: "2",
      estimulo: "visual",
    },
  ];

  return (
    <div className="modulo-social">
      <div className="social-header">
        <h1 className="titulo-social">🤝 Módulo Social</h1>
        <p className="descripcion-social">
          Escoge una de las siguientes actividades para fortalecer tus habilidades sociales 🌱
        </p>
      </div>

      <div className="contenedor-social">
        {actividades.map((act, i) => (
          <div
            key={i}
            className="card-social"
            onClick={() => seleccionarActividad(act.titulo)}
          >
            <h2>{act.titulo}</h2>
            <p>{act.descripcion}</p>
            <p className="objetivo">{act.objetivo}</p>
            <p className="info">
              Dificultad: <strong>{act.dificultad}</strong> · Estímulo:{" "}
              <strong>{act.estimulo}</strong>
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
